import com.eviware.soapui.support.XmlHolder
import com.eviware.soapui.impl.wsdl.testcase.WsdlTestRunContext
def vName=testRunner.testCase.testSuite.testCases["addemployee"].getPropertyValue("name")
def vOutReq=testRunner.testCase.testSuite.testCases["getemployeedetails"].testSteps["getstep"].getPropertyValue("Request")
def vXMLHolder=new XmlHolder(vOutReq)
vXMLHolder.setNodeValue("//typ:employeeName",vName)
def vGetXML=vXMLHolder.getXml()
testRunner.testCase.testSuite.testCases["getemployeedetails"].testSteps["getstep"].setPropertyValue("Request",vGetXML)
def vContext=testRunner.testCase.testSuite.testCases["getemployeedetails"].testSteps["getstep"]
def vRun=new WsdlTestRunContext(vContext)
vContext.run(testRunner,vRun)
def vResponse=testRunner.testCase.testSuite.testCases["getemployeedetails"].testSteps["getstep"].getPropertyValue("Response")
def vGetRes=new XmlHolder(vResponse)
def vNameRes=vGetRes.getNodeValue("//ns:name")
assert vNameRes==vName
